mapper.update:
UPDATE weight_record
record_date = #{recordDate}, weight = #{weight}, bmi = #{bmi}, body_fat_rate = #{bodyFatRate}, muscle_mass = #{muscleMass}, bone_mass = #{boneMass}, water_rate = #{waterRate}, visceral_fat = #{visceralFat}, basal_metabolism = #{basalMetabolism}, notes = #{notes}, updated_at = #{updatedAt}
WHERE id = #{id}

mapper.select:
SELECT * FROM weight_record WHERE user_id = #{userId}

    AND record_date >= #{query.startDate}


    AND record_date <= #{query.endDate}

ORDER BY record_date DESC

    LIMIT #{query.pageSize} OFFSET #{(query.pageNum-1) * query.pageSize}

SELECT * FROM weight_record 
WHERE user_id = #{userId} 
AND record_date BETWEEN #{startDate} AND #{endDate}
ORDER BY record_date ASC

SELECT * FROM weight_record 
WHERE user_id = #{userId} 
AND record_date < #{recordDate}
ORDER BY record_date DESC 
LIMIT 1
